                                                                                                                                                                ----------------------------------------------------------------
                                                                                                                                                                -- HELPERS
                                                                                                                                                                ----------------------------------------------------------------
                                                                                                                                                                local function csc_getSafeItemName(itemID)
                                                                                                                                                                    local item = getItem(itemID)
                                                                                                                                                                        return (item and item:getName()) or ("ID " .. tostring(itemID))
                                                                                                                                                                        end

                                                                                                                                                                        local function csc_isSellable(itemID)
                                                                                                                                                                            return csc_config.ITEM_PRICES[itemID] ~= nil
                                                                                                                                                                            end

                                                                                                                                                                            local function csc_getPrice(itemID)
                                                                                                                                                                                local price = csc_config.ITEM_PRICES[itemID] or 0
                                                                                                                                                                                    if csc_config.EVENT_ACTIVE then
                                                                                                                                                                                            price = price * (csc_config.EVENT_MULTIPLIER or 1)
                                                                                                                                                                                                end
                                                                                                                                                                                                    return price
                                                                                                                                                                                                    end

                                                                                                                                                                                                    local function csc_calculateTotalWlsForItem(itemID, amount)
                                                                                                                                                                                                        if not csc_isSellable(itemID) then
                                                                                                                                                                                                                return 0
                                                                                                                                                                                                                    end
                                                                                                                                                                                                                        local price = csc_getPrice(itemID)
                                                                                                                                                                                                                            return price * amount
                                                                                                                                                                                                                            end

                                                                                                                                                                                                                            local function csc_calculateCurrencyBreakdown(totalWls)
                                                                                                                                                                                                                                local breakdown = { bbgl = 0, bgl = 0, dl = 0, wl = 0 }
                                                                                                                                                                                                                                    breakdown.bbgl = math.floor(totalWls / 1000000)
                                                                                                                                                                                                                                        local remainder = totalWls % 1000000

                                                                                                                                                                                                                                            breakdown.bgl = math.floor(remainder / 10000)
                                                                                                                                                                                                                                                local remainder2 = remainder % 10000

                                                                                                                                                                                                                                                    breakdown.dl = math.floor(remainder2 / 100)
                                                                                                                                                                                                                                                        breakdown.wl = remainder2 % 100

                                                                                                                                                                                                                                                            return breakdown
                                                                                                                                                                                                                                                            end

                                                                                                                                                                                                                                                            ----------------------------------------------------------------
                                                                                                                                                                                                                                                            -- SAVE / LOAD CONFIG
                                                                                                                                                                                                                                                            ----------------------------------------------------------------
                                                                                                                                                                                                                                                            local function csc_saveConfig()
                                                                                                                                                                                                                                                                saveDataToServer(csc_config.SAVE_KEY, csc_config)
                                                                                                                                                                                                                                                                end

                                                                                                                                                                                                                                                                local function csc_loadConfig()
                                                                                                                                                                                                                                                                    local data = loadDataFromServer(csc_config.SAVE_KEY)
                                                                                                                                                                                                                                                                        if data and type(data) == "table" then
                                                                                                                                                                                                                                                                                if data.ITEM_PRICES and type(data.ITEM_PRICES) == "table" then
                                                                                                                                                                                                                                                                                            for id, price in pairs(data.ITEM_PRICES) do
                                                                                                                                                                                                                                                                                                            csc_config.ITEM_PRICES[id] = price
                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                end

                                                                                                                                                                                                                                                                                                                                        if data.EVENT_ACTIVE ~= nil then
                                                                                                                                                                                                                                                                                                                                                    csc_config.EVENT_ACTIVE = data.EVENT_ACTIVE
                                                                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                                                                    if data.EVENT_MULTIPLIER then
                                                                                                                                                                                                                                                                                                                                                                                csc_config.EVENT_MULTIPLIER = data.EVENT_MULTIPLIER
                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                            else
                                                                                                                                                                                                                                                                                                                                                                                                    csc_saveConfig()
                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                        -- PLAYER MENU
                                                                                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                        local function csc_buildPlayerMenu(player)
                                                                                                                                                                                                                                                                                                                                                                                                            local netID = player:getNetID()
                                                                                                                                                                                                                                                                                                                                                                                                                csc_sessionState[netID] = csc_sessionState[netID] or {}
