                                                                                                                                                                                                                                                                                                                                                                                                                        local totalWls = 0
                                                                                                                                                                                                                                                                                                                                                                                                                            local totalItems = 0

                                                                                                                                                                                                                                                                                                                                                                                                                                local dialog = "set_bg_color|0,50,70,127|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                    dialog = dialog .. "set_border_color|0,255,255,255|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "text_scaling_string|aaaaaaaaaaaaaaaaaa|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                            dialog = dialog .. "add_label_with_icon|big|`wCrystal Selling System|left|2248|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                dialog = dialog .. "add_smalltext|`oSell Geiger loot & crystals for World Locks based on configured prices.|left|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                    dialog = dialog .. "add_spacer|small|\n"

                                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "add_label|small|`wYour Crystals:`o|\n"

                                                                                                                                                                                                                                                                                                                                                                                                                                                            for id, basePrice in pairs(csc_config.ITEM_PRICES) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local amount = player:getItemAmount(id) or 0
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if amount > 0 then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local wls = csc_calculateTotalWlsForItem(id, amount)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    totalWls = totalWls + wls
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                totalItems = totalItems + amount

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            dialog = dialog .. "add_textbox|`o- `w" .. csc_getSafeItemName(id) ..
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            "`o: `w" .. amount .. "`o pcs → `w" .. wls .. " WLs`o|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if totalItems == 0 then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    dialog = dialog .. "add_textbox|`4You don't have any crystals or Geiger loot to sell.`o|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        else
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                dialog = dialog .. "add_spacer|small|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "add_textbox|`wTotal items: `2" .. totalItems ..
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    "`o  •  Estimated reward: `2" .. totalWls .. " WLs`o|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            dialog = dialog .. "add_spacer|small|\n"

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if totalItems > 0 and totalWls > 0 then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "add_button|csc_sell_all|`2Sell All Crystals|noflags|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                dialog = dialog .. "add_spacer|small|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- AUTO-SELL SETTINGS (per UID)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            local uid = player:getUserID()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                local isPickup = csc_autoSellPickup[uid] and "1" or "0"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local isGeiger = csc_autoSellGeiger[uid] and "1" or "0"

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "add_textbox|`wAuto-Sell Settings:`o|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            dialog = dialog .. "add_checkbox|csc_autosell_pickup|`wAuto-Sell on Pickup|" .. isPickup .. "|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                dialog = dialog .. "add_checkbox|csc_autosell_geiger|`wAuto-Sell on Geiger|" .. isGeiger .. "|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    dialog = dialog .. "add_button|csc_save_settings|`2Save Auto-Sell Settings|noflags|\n"

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        dialog = dialog .. "add_spacer|small|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            dialog = dialog .. "add_quick_exit|\n"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                dialog = dialog .. "end_dialog|csc_player_menu|Close||\n"

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    player:onDialogRequest(dialog)

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        state.previewTotalWls   = totalWls
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            state.previewTotalItems = totalItems
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end

